mapper.select:
select ri.id, room_number, rent, ri.is_release, la.lease_end_date, la.room_id is not null is_check_in, ai.id apartment_id, ai.name, ai.introduction, ai.district_id, ai.district_name, ai.city_id, ai.city_name, ai.province_id, ai.province_name, ai.address_detail, ai.latitude, ai.longitude, ai.phone, ai.is_release apartment_is_release from (select id, room_number, rent, apartment_id, is_release from room_info where is_deleted = 0) ri left join (select room_id, lease_end_date from lease_agreement where is_deleted = 0 and status in ( ${@com.atguigu.lease.model.enums.LeaseStatus@SIGNED.getCode()}, ${@com.atguigu.lease.model.enums.LeaseStatus@WITHDRAWING.getCode()} )) la on ri.id = la.room_id left join (select id, name, introduction, district_id, district_name, city_id, city_name, province_id, province_name, address_detail, latitude, longitude, phone, is_release from apartment_info where is_deleted = 0) ai on ri.apartment_id = ai.id
ai.province_id = #{queryVo.provinceId}
and ai.city_id = #{queryVo.cityId}
and ai.district_id = #{queryVo.districtId}
and ri.apartment_id = #{queryVo.apartmentId}
